import { Link } from 'react-router-dom'
import styled from 'styled-components'

const ArtistsWrapper = styled.div`
  position: absolute;
  top: 20vw;
  left: 5vw;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
`

const ArtistWrapper = styled.div`
  display: flex;
  flex-direction: column;
  font-weight: 700;
  font-size: min(3vw, 5em);
  line-height: 0.9em;
  width: 20vw;
  max-width: 20vw;
  height: 30vh;
  border: 1px solid #000000;
  margin: 10px;
  padding: 10px;
`
const Number = styled.div`
    position: absolute;
`

export const ARTISTS = [
  { id: 0, name: "ronnie-makebelieve", album: "poop bum", song: "sleep.wav", description: "chill ronnies" },
  { id: 2, name: "sleep-300", album: "poop bum", song: "sleep.wav", description: "chill dudes" },
  { id: 3, name: "sleep-300", album: "poop bum",  song: "sleep.wav", description: "chill dudes" },
];

export function Artists() {
  return (
    <ArtistsWrapper>
        {ARTISTS.map((artist, index) => {
            return <Artist artist={artist} index={index} />
        })}
    </ArtistsWrapper>
  )
}

function Artist({artist, index}) {
    return (
        <div className="item" key={index}>
          <figure className="item__fig js-plane">
            <div className="aspect" style={{aspect: "151.75%"}}></div>
            <img className="item__img" src="kshack-logo.jpg" alt={`${artist.name} image`} />
          </figure>
          <span className="item__pretitle">{artist.name}</span>
          <h2 className="item__title">{artist.album}</h2>
          <span className="item__counter">{index < 10 ? `0${index}` : index}</span>
          <p className="item__description">{artist.description}</p>
        </div>
    )
}
