import * as THREE from 'three'
import { Canvas, useLoader } from '@react-three/fiber'
import { useMemo } from 'react'
import { TextureLoader } from "three/src/loaders/TextureLoader";

// import vertex from '../assets/vertex.glsl'
// import fragment from '../assets/fragment.glsl'

const fragmentShader = `
varying vec3 Normal;
varying vec3 Position;

uniform vec3 Ka;
uniform vec3 Kd;
uniform vec3 Ks;
uniform vec4 LightPosition;
uniform vec3 LightIntensity;
uniform float Shininess;

vec3 phong() {
  vec3 n = normalize(Normal);
  vec3 s = normalize(vec3(LightPosition) - Position);
  vec3 v = normalize(vec3(-Position));
  vec3 r = reflect(-s, n);

  vec3 ambient = Ka;
  vec3 diffuse = Kd * max(dot(s, n), 0.0);
  vec3 specular = Ks * pow(max(dot(r, v), 0.0), Shininess);

  return LightIntensity * (ambient + diffuse + specular);
}

void main() {
  vec3 blue = vec3(0.0, 0.0, 1.0);
  gl_FragColor = vec4(blue*phong(), 1.0);
}`

const vertexShader = `
varying vec3 Normal;
varying vec3 Position;

void main() {
  Normal = normalize(normalMatrix * normal);
  Position = vec3(modelViewMatrix * vec4(position, 1.0));
  gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
}
`
/*
 Load texture into shader
 https://codesandbox.io/s/divine-shape-wqf6f?from-embed=&file=/src/App.js:90-155
*/
export function ArtistImage({src, index}) {
  const texture = useLoader(THREE.TextureLoader, src);
  console.log('Texture', texture)
  const data = useMemo(
      () => ({
          uniforms: {
              uTexture: { value: {
                  texture: {
                      ...texture,
                      generateMipmaps: false,
                  } 
              }},
              uTime: { value: 0 },
              uProg: { value: 0 },
              uIndex: { value: index },
          },
          fragmentShader,
          vertexShader
      }),
      []
  )

return (
  <Canvas>
      <mesh>
      <planeBufferGeometry attach="geometry" args={[320, 320]} />
      {/* <shaderMaterial attach="material" {...data} /> */}
      <meshBasicMaterial attach="material" map={texture} />
      </mesh>
  </Canvas>
)
}
