import { Link } from 'react-router-dom'
import styled from 'styled-components'

const ArtistsWrapper = styled.div`
  position: absolute;
  top: 20vw;
  left: 5vw;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
`

const ArtistWrapper = styled.div`
  display: flex;
  flex-direction: column;
  font-weight: 700;
  font-size: min(3vw, 5em);
  line-height: 0.9em;
  width: 20vw;
  max-width: 20vw;
  height: 30vh;
  border: 1px solid #000000;
  margin: 10px;
  padding: 10px;
`
const Number = styled.div`
    position: absolute;

`

export const ARTISTS = [
  { id: 0, name: "ronnie-makebelieve", song: "sleep.wav", description: "chill ronnies" },
  { id: 1, name: "sleep-300", song: "sleep.wav", description: "chill dudes" },
  { id: 1, name: "sleep-300", song: "sleep.wav", description: "chill dudes" },
  { id: 1, name: "sleep-300", song: "sleep.wav", description: "chill dudes" },
  { id: 1, name: "sleep-300", song: "sleep.wav", description: "chill dudes" },
  { id: 1, name: "sleep-300", song: "sleep.wav", description: "chill dudes" },
  { id: 1, name: "sleep-300", song: "sleep.wav", description: "chill dudes" },
  { id: 1, name: "sleep-300", song: "sleep.wav", description: "chill dudes" },
  { id: 1, name: "sleep-300", song: "sleep.wav", description: "chill dudes" },

];

export function Artists() {
  return (
    <ArtistsWrapper>
        {ARTISTS.map((artist, index) => {
            return <Artist artist={artist} index={index} />
        })}
    </ArtistsWrapper>
  )
}

function Artist({artist, index}) {
    return (
        <div class="item">
          <figure class="item__fig js-plane">
            <div class="aspect" style="--aspect: 151.75%"></div>
            <img class="item__img" src="img/img-01.jpg" alt="Some image" />
          </figure>
          <span class="item__pretitle">{artist.name}</span>
          <h2 class="item__title">Actuator</h2>
          <span class="item__counter">{index < 10 ? `0{index}` : index}</span>
          <p class="item__description">Life lived in the absence of the psychedelic experience is life trivialized, life denied, life enslaved to the ego.</p>
        </div>
    )
}
