import { useState } from 'react'
import { Link, useRouteMatch, useParams } from 'react-router-dom'
import { SoundVisualizer } from './SoundVisualizer'

export const ARTISTS = [
  { id: 0, name: "ronny-makebelieve", song: "sleep.wav", description: "chill dudes" },
  { id: 1, name: "sleep-300", song: "sleep.wav", description: "chill dudes" },
];

export function ArtistPage() {
  const [ready, set] = useState(false)
  let { name } = useParams()
  console.log(name)
  const currentArtist = ARTISTS.find(artist => artist.name === name)
  console.log(currentArtist)
  return (
    <>
      {ready && <SoundVisualizer />}
      <div className='main-content'>
        <h1>{ name }</h1>
        <p>{ currentArtist.song }</p>
        <p>{ currentArtist.description }</p>
      </div>
      <div className={`fullscreen bg ${ready ? 'ready' : 'notready'} ${ready && 'clicked'}`}>
        <div className="stack">
          <button onClick={() => set(true)}>▶️</button>
        </div>
      </div>
    </>
  )
}
